mapper.select:
SELECT count(1)
FROM t_shop t , t_member m WHERE t.is_deleted = 0 AND m.is_deleted = 0
AND t.member_id=m.id

    and t.id=#{shop.id}


    and t.audit_status=#{shop.auditStatus}


    and t.member_id=#{shop.memberId}


    and t.shop_name like concat(concat('%', #{shop.shopName}), '%')


    and t.province=#{shop.province}


    and t.city=#{shop.city}


    and t.county=#{shop.county}


    and (m.province IN

        #{item}

    or m.city IN

        #{item}

    or m.county IN

        #{item}

    )



    and (m.province NOT IN

        #{item}

    or m.city NOT IN

        #{item}

    or m.county NOT IN

        #{item}

    )


    and t.create_time > #{shop.startCreateTime}


    and t.create_time < #{shop.endCreateTime}

SELECT s.*,t.name AS shopMember ,
(SELECT COUNT(1) FROM t_shop_member WHERE shop_id=t.id) AS staffNum,
(SELECT COUNT(1) FROM t_cars WHERE shop_id=t.id) AS shopCarNum,
(SELECT COUNT(1) FROM t_shop_allied WHERE (shop_id=t.id OR allied_shop_id=t.id) ) AS alliedNum
FROM t_shop s , (
SELECT t.* FROM t_member t , t_user_member u WHERE t.is_deleted = 0 AND t.id=u.member_id

    and u.user_id=#{shop.userId}

union
select t.* from t_member t where t.is_deleted = 0
) t
WHERE s.is_deleted = 0 AND t.is_deleted = 0
AND s.member_id=t.id


    and s.audit_status=#{shop.auditStatus}


    and s.member_id=#{shop.memberId}


    and s.shop_name like concat(concat('%', #{shop.shopName}), '%')


    and t.phone =#{shop.phone}



    and t.province=#{shop.province}


    and t.city=#{shop.city}


    and t.county=#{shop.county}


    and (m.province IN

        #{item}

    or m.city IN

        #{item}

    or m.county IN

        #{item}

    )



    and (m.province NOT IN

        #{item}

    or m.city NOT IN

        #{item}

    or m.county NOT IN

        #{item}

    )
